+  content: string;
+  length: number;
+  hashtags: string[];
+}
+
+export interface CommitStats {
+  totalCommits: number;
+  totalFilesChanged: number;
+  totalWeight: number;
+  signals: { [key: string]: number };
+  impacts: {
+    HIGH_RISK: number;
+    MEDIUM_RISK: number;
+    LOW_RISK: number;
+  };
+  feature: string;
+  repoCount: number;
+  repos: string[];
+}
+
+export interface GeneratePostsResponse {
+  success: boolean;
+  data: {
+    username: string;
+    repo?: string;
+    tones: string[];
+    posts: Post[];
+    stats: CommitStats;
+  };
+  error?: string;
+}
+
+/**
+ * Generate LinkedIn posts from GitHub commits
+ */
+export async function generatePosts(
+  request: GeneratePostsRequest
+): Promise<GeneratePostsResponse> {
+  const response = await fetch(`${API_BASE_URL}/generate`, {
+    method: 'POST',
+    headers: {
+      'Content-Type': 'application/json',
+    },
+    body: JSON.stringify(request),
+  });
+
+  if (!response.ok) {
+    const errorData = await response.json().catch(() => ({}));
+    throw new Error(
+      errorData.message || 
+      errorData.error || 
+      `HTTP ${response.status}: ${response.statusText}`
+    );
+  }
+
+  return response.json();
+}
+
+/**
+ * Health check endpoint
+ */
+export async function healthCheck(): Promise<{ status: string; timestamp: string }> {
+  const response = await fetch(`${API_BASE_URL}/health`);
+  
+  if (!response.ok) {
+    throw new Error('Backend is not responding');
+  }
+  
+  return response.json();
+}
+
+/**
+ * Validate GitHub username
+ */
+export async function validateGitHubUsername(
+  username: string
+): Promise<{ valid: boolean; message?: string }> {
+  const response = await fetch(
+    `${API_BASE_URL}/validate-username?username=${encodeURIComponent(username)}`
+  );
+  
+  if (!response.ok) {
+    throw new Error('Failed to validate username');
+  }
+  
+  return response.json();
+}
